SELECT tat.id id, tat.proc_inst_id procInstId, tat.execution_id executionId, tat.current_task_id currentTaskId , tat.current_task_key currentTaskKey, tat.ask_task_key askTaskKey, tat.is_ask_end isAskEnd, tat.create_time createTime, tat.update_time updateTime, tat.create_id createId, tat.update_id updateId, tat.ask_user_id askUserId, tat.ask_comment askComment, tat.answer_comment answerComment FROM t_ask_task tat WHERE tat.create_id = #{createId}
AND tat.ask_user_id = #{askUserId}
AND tat.current_task_key = #{currentTaskKey}
AND tat.ask_task_key = #{askTaskKey}
AND tat.is_ask_end = #{askEnd}

